// Rename file with proper convention
const path = require('path');
const fs = require('fs');

const filePath = $input.item.json.path;
const fileName = path.basename(filePath);
const dirName = path.dirname(filePath);
const ext = path.extname(fileName).toLowerCase();

// Get current timestamp
const now = new Date();
const timestamp = now.toISOString().slice(0,19).replace(/[-:T]/g, '').replace(/(.{8})(.{6})/, '$1_$2');

// Determine type based on extension
let fileType = 'photo';
if (['.mp4', '.mov', '.avi', '.webm'].includes(ext)) fileType = 'video';
if (['.ogg', '.mp3', '.wav', '.m4a'].includes(ext)) fileType = 'audio';

// New filename: YYYYMMDD_HHMMSS_Sicily_type.ext
const newFileName = `${timestamp}_Sicily_${fileType}${ext}`;
const newPath = path.join(dirName, newFileName);

// Rename the file
if (filePath !== newPath && fs.existsSync(filePath)) {
  fs.renameSync(filePath, newPath);
}

return {
  originalPath: filePath,
  newPath: newPath,
  fileName: newFileName,
  fileType: fileType,
  timestamp: now.toISOString(),
  size: fs.statSync(newPath).size
};

// Log to manifest
const fs = require('fs');
const manifestPath = '/home/angel/Downloads/Telegram Desktop/eco-2026/manifest.json';

let manifest = [];
if (fs.existsSync(manifestPath)) {
  manifest = JSON.parse(fs.readFileSync(manifestPath, 'utf8'));
}

const entry = {
  id: manifest.length + 1,
  originalFile: $('Rename File').item.json.originalPath,
  newFile: $('Rename File').item.json.newPath,
  fileName: $('Rename File').item.json.fileName,
  fileType: $('Rename File').item.json.fileType,
  size: $('Rename File').item.json.size,
  uploadedAt: new Date().toISOString(),
  googlePhotosId: $json.id || 'pending',
  status: 'uploaded'
};

manifest.push(entry);
fs.writeFileSync(manifestPath, JSON.stringify(manifest, null, 2));

return entry;
